import { useState, useEffect } from "react";
import JadwalCard from "../components/JadwalCard";
import JadwalForm from "../components/JadwalForm";
import {
  getJadwalList,
  createJadwal,
  updateJadwal,
  deleteJadwal,
} from "../api/jadwalApi";


const Jadwal = () => {
  const [jadwalList, setJadwalList] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [editData, setEditData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  useEffect(() => {
    fetchJadwal();
  }, []);

  const fetchJadwal = async () => {
    setLoading(true);
    setError("");
    try {
      const data = await getJadwalList();
      setJadwalList(data);
    } catch (err) {
      setError("Gagal mengambil data jadwal");
    } finally {
      setLoading(false);
    }
  };

  const handleTambah = async (jadwalBaru) => {
    setError("");
    try {
      await createJadwal(jadwalBaru);
      fetchJadwal();
      setShowForm(false);
    } catch (err) {
      setError(err.message || "Gagal menambah jadwal");
    }
  };

  const handleEdit = async (updatedJadwal) => {
    setError("");
    try {
      await updateJadwal(updatedJadwal.id, updatedJadwal);
      fetchJadwal();
      setEditData(null);
      setShowForm(false);
    } catch (err) {
      setError(err.message || "Gagal mengupdate jadwal");
    }
  };

  const handleDelete = async (id) => {
    setError("");
    try {
      await deleteJadwal(id);
      fetchJadwal();
    } catch (err) {
      setError(err.message || "Gagal menghapus jadwal");
    }
  };

  return (
    <div className="p-6">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-2xl font-bold text-green-800">Jadwal Perawatan</h1>
        <button
          onClick={() => {
            setShowForm(!showForm);
            setEditData(null);
          }}
          className="bg-yellow-600 hover:bg-yellow-700 text-white px-4 py-2 rounded"
        >
          {showForm ? "Batal" : "+ Tambah Jadwal"}
        </button>
      </div>

      {showForm && (
        <div className="mb-6">
          <JadwalForm
            onSubmit={editData ? handleEdit : handleTambah}
            initialData={editData}
            isEdit={!!editData}
          />
        </div>
      )}

      {error && <div className="text-red-600">{error}</div>}
      {loading ? (
        <div>Loading...</div>
      ) : (
        <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-3">
          {jadwalList.map((jadwal) => (
            <JadwalCard
              key={jadwal.id}
              jadwal={jadwal}
              onEdit={() => {
                setEditData(jadwal);
                setShowForm(true);
              }}
              onDelete={() => handleDelete(jadwal.id)}
            />
          ))}
        </div>
      )}
    </div>
  );
};

export default Jadwal;
