import { useState } from "react";
import TanamanCard from "../components/TanamanCard";
import TanamanForm from "../components/TanamanForm";

const Tanaman = () => {
  const [tanamanList, setTanamanList] = useState([]);
  const [tanamanEdit, setTanamanEdit] = useState(null); // ← untuk edit

  const handleAddOrEdit = (newTanaman) => {
    if (tanamanEdit) {
      // Edit: update tanaman yang ada
      setTanamanList((prev) =>
        prev.map((tanaman) =>
          tanaman.id === newTanaman.id ? newTanaman : tanaman
        )
      );
      setTanamanEdit(null); // reset setelah edit
    } else {
      // Tambah baru
      setTanamanList([...tanamanList, newTanaman]);
    }
  };

  const handleDelete = (id) => {
    setTanamanList(tanamanList.filter((tanaman) => tanaman.id !== id));
  };

  const handleEdit = (tanaman) => {
    setTanamanEdit(tanaman); // isi data yang mau diedit
  };

  return (
    <div className="p-4 space-y-6">
      <TanamanForm
        onSubmit={handleAddOrEdit}
        initialData={tanamanEdit}
        isEdit={!!tanamanEdit}
      />

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
        {tanamanList.map((tanaman) => (
          <TanamanCard
            key={tanaman.id}
            tanaman={tanaman}
            onDelete={handleDelete}
            onEdit={handleEdit}
          />
        ))}
      </div>
    </div>
  );
};

export default Tanaman;
