import { useState, useEffect } from "react";
import TanamanForm from "../components/TanamanForm";
import TanamanCard from "../components/TanamanCard";
import {
  getTanamanList,
  createTanaman,
  updateTanaman,
  deleteTanaman,
} from "../api/plantApi";


const Tanaman = () => {
  const [tanamanList, setTanamanList] = useState([]);
  const [editingTanaman, setEditingTanaman] = useState(null);
  const [showForm, setShowForm] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  useEffect(() => {
    fetchTanaman();
  }, []);

  const fetchTanaman = async () => {
    setLoading(true);
    setError("");
    try {
      const data = await getTanamanList();
      setTanamanList(data);
    } catch (err) {
      setError("Gagal mengambil data tanaman");
    } finally {
      setLoading(false);
    }
  };

  const handleAddOrEdit = async (data) => {
    setError("");
    try {
      if (editingTanaman) {
        await updateTanaman(data.id, data);
        setEditingTanaman(null);
      } else {
        await createTanaman(data);
      }
      fetchTanaman();
      setShowForm(false);
    } catch (err) {
      setError(err.message || "Gagal menyimpan data");
    }
  };

  const handleDelete = async (id) => {
    setError("");
    try {
      await deleteTanaman(id);
      fetchTanaman();
    } catch (err) {
      setError(err.message || "Gagal menghapus data");
    }
  };

  const handleEdit = (item) => {
    setEditingTanaman(item);
    setShowForm(true);
    window.scrollTo({ top: 0, behavior: "smooth" });
  };

  return (
    <div className="p-6">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-2xl font-bold text-green-800">Jenis Tanaman</h1>
        <button
          onClick={() => {
            setShowForm(!showForm);
            setEditingTanaman(null);
          }}
          className="bg-green-600 hover:bg-green-700 text-white px-4 py-2 rounded"
        >
          {showForm ? "Batal" : "+ Tambah Tanaman"}
        </button>
      </div>

      {showForm && (
        <div className="mb-6">
          <TanamanForm
            onSubmit={handleAddOrEdit}
            initialData={editingTanaman}
            isEdit={!!editingTanaman}
          />
        </div>
      )}

      {error && <div className="text-red-600">{error}</div>}
      {loading ? (
        <div>Loading...</div>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
          {tanamanList.map((tanaman) => (
            <TanamanCard
              key={tanaman.id}
              tanaman={tanaman}
              onDelete={handleDelete}
              onEdit={handleEdit}
            />
          ))}
        </div>
      )}
    </div>
  );
};

export default Tanaman;
