import { useState, useEffect, useCallback } from "react";
import TanamanForm from "../components/TanamanForm";
import TanamanCard from "../components/TanamanCard";
import { tanamanApi } from "../services/api";

const Tanaman = () => {
  const [tanamanList, setTanamanList] = useState([]);
  const [editingTanaman, setEditingTanaman] = useState(null);
  const [showForm, setShowForm] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [pagination, setPagination] = useState({
    limit: 10,
    offset: 0,
    total: 0,
  });
  const [searchTerm, setSearchTerm] = useState("");

  // Fungsi untuk mengambil data tanaman dari API - menggunakan useCallback
  const fetchTanaman = useCallback(async () => {
    setLoading(true);
    setError(null);
    try {
      console.log(
        `Fetching tanaman data (limit: ${pagination.limit}, offset: ${pagination.offset})...`
      );

      const response = await tanamanApi.getAll(
        pagination.limit,
        pagination.offset,
        searchTerm
      );
      console.log("Received tanaman data:", response);

      if (response && response.tanaman && Array.isArray(response.tanaman)) {
        setTanamanList(response.tanaman);
        setPagination((prev) => ({
          ...prev,
          total: response.pagination?.total || prev.total,
        }));
      } else {
        setTanamanList([]);
      }
    } catch (err) {
      console.error("Error fetching tanaman:", err);
      setError(`Gagal mengambil data tanaman: ${err.message || "Unknown error"}`);
      setTanamanList([]);
    } finally {
      setLoading(false);
    }
  }, [pagination.limit, pagination.offset, searchTerm]);

  // Ambil data tanaman saat mount atau pagination berubah
  useEffect(() => {
    fetchTanaman();
  }, [fetchTanaman]);

  const handleAddOrEdit = async (data) => {
    setLoading(true);
    setError(null);
    try {
      if (editingTanaman) {
        // Update tanaman
        await tanamanApi.update(data.id, data);
        // Refresh data
        fetchTanaman();
        setEditingTanaman(null);
      } else {
        // Tambah tanaman baru
        await tanamanApi.create(data);
        // Refresh data
        fetchTanaman();
      }
      setShowForm(false);
    } catch (err) {
      setError(
        editingTanaman
          ? "Gagal mengupdate tanaman"
          : "Gagal menambah tanaman"
      );
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm("Apakah Anda yakin ingin menghapus tanaman ini?")) {
      return;
    }

    setLoading(true);
    setError(null);
    try {
      await tanamanApi.delete(id);
      fetchTanaman();
    } catch (err) {
      setError("Gagal menghapus tanaman");
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const handleEdit = (item) => {
    setEditingTanaman(item);
    setShowForm(true);
    window.scrollTo({ top: 0, behavior: "smooth" });
  };

  // Handle pagination changes
  const nextPage = () => {
    if (pagination.offset + pagination.limit < pagination.total) {
      setPagination((prev) => ({
        ...prev,
        offset: prev.offset + prev.limit,
      }));
    }
  };

  const prevPage = () => {
    if (pagination.offset > 0) {
      setPagination((prev) => ({
        ...prev,
        offset: Math.max(0, prev.offset - prev.limit),
      }));
    }
  };

  // Handle search
  const handleSearch = (e) => {
    e.preventDefault();
    // Reset pagination when searching
    setPagination((prev) => ({ ...prev, offset: 0 }));
    fetchTanaman();
  };

  // Handle per page change
  const handlePerPageChange = (e) => {
    const newLimit = parseInt(e.target.value);
    setPagination((prev) => ({
      ...prev,
      limit: newLimit,
      offset: 0, // Reset to first page when changing items per page
    }));
  };

  return (
    <div className="p-6">
      {/* Header & Tombol Tambah */}
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-2xl font-bold text-green-800">Jenis Tanaman</h1>
        <button
          onClick={() => setShowForm(!showForm)}
          className="bg-green-600 hover:bg-green-700 text-white px-4 py-2 rounded"
        >
          {showForm ? "Batal" : "+ Tambah Tanaman"}
        </button>
      </div>      {/* Error message */}
      {error && (
        <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
          {error}
        </div>
      )}

      {/* Loading indicator */}
      {loading && (
        <div className="flex justify-center my-4">
          <div className="animate-spin rounded-full h-10 w-10 border-t-2 border-b-2 border-green-500"></div>
        </div>
      )}
      
      {/* Search bar */}
      <div className="mb-6 bg-white p-4 rounded shadow">
        <form onSubmit={handleSearch} className="flex gap-2">
          <input
            type="text"
            placeholder="Cari tanaman..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="flex-grow p-2 border border-gray-300 rounded focus:outline-none focus:border-green-500"
          />
          <button 
            type="submit"
            className="bg-green-600 hover:bg-green-700 text-white px-4 py-2 rounded"
          >
            Cari
          </button>
          <button
            type="button"
            onClick={() => {
              setSearchTerm('');
              setPagination({ ...pagination, offset: 0 });
              fetchTanaman();
            }}
            className="bg-gray-300 hover:bg-gray-400 text-gray-800 px-4 py-2 rounded"
          >
            Reset
          </button>
        </form>
      </div>

      {showForm && (
        <div className="mb-6">
          <TanamanForm
            onSubmit={handleAddOrEdit}
            initialData={editingTanaman}
            isEdit={!!editingTanaman}
          />
        </div>
      )}

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
        {tanamanList.map((tanaman) => (
          <TanamanCard
            key={tanaman.id}
            tanaman={tanaman}
            onDelete={handleDelete}
            onEdit={handleEdit}
          />
        ))}
      </div>

      {/* Empty state */}
      {!loading && tanamanList.length === 0 && (
        <div className="text-center py-10">
          <p className="text-gray-500">
            Belum ada data tanaman. Silakan tambahkan.
          </p>
        </div>
      )}

      {/* Pagination controls */}
      <div className="flex justify-between items-center mt-6">
        <div>
          <button
            onClick={prevPage}
            disabled={pagination.offset === 0}
            className="bg-green-600 hover:bg-green-700 text-white px-4 py-2 rounded-l"
          >
            &laquo; Sebelumnya
          </button>
          <button
            onClick={nextPage}
            disabled={
              pagination.offset + pagination.limit >= pagination.total
            }
            className="bg-green-600 hover:bg-green-700 text-white px-4 py-2 rounded-r"
          >
            Selanjutnya &raquo;
          </button>
        </div>

        {/* Items per page selector */}
        <div>
          <label className="text-gray-700 mr-2">
            Tampilkan:
            <select
              value={pagination.limit}
              onChange={handlePerPageChange}
              className="ml-2 bg-white border border-gray-300 rounded"
            >
              <option value={5}>5</option>
              <option value={10}>10</option>
              <option value={20}>20</option>
            </select>
            &nbsp;data
          </label>
        </div>
      </div>
    </div>
  );
};

export default Tanaman;
