import { useState, useEffect } from "react";
import TanamanForm from "../components/TanamanForm";
import TanamanCard from "../components/TanamanCard";
import { tanamanApi } from "../services/api";

const Tanaman = () => {
  const [tanamanList, setTanamanList] = useState([]);
  const [editingTanaman, setEditingTanaman] = useState(null);
  const [showForm, setShowForm] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Ambil data tanaman saat mount
  useEffect(() => {
    fetchTanaman();
  }, []);

  // Fungsi untuk mengambil data tanaman dari API
  const fetchTanaman = async () => {
    setLoading(true);
    setError(null);
    try {
      const data = await tanamanApi.getAll();
      setTanamanList(data);
    } catch (err) {
      setError("Gagal mengambil data tanaman");
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const handleAddOrEdit = async (data) => {
    setLoading(true);
    setError(null);
    try {
      if (editingTanaman) {
        // Update tanaman
        await tanamanApi.update(data.id, data);
        // Refresh data
        fetchTanaman();
        setEditingTanaman(null);
      } else {
        // Tambah tanaman baru
        await tanamanApi.create(data);
        // Refresh data
        fetchTanaman();
      }
      setShowForm(false);
    } catch (err) {
      setError(
        editingTanaman
          ? "Gagal mengupdate tanaman"
          : "Gagal menambah tanaman"
      );
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm("Apakah Anda yakin ingin menghapus tanaman ini?")) {
      return;
    }

    setLoading(true);
    setError(null);
    try {
      await tanamanApi.delete(id);
      fetchTanaman();
    } catch (err) {
      setError("Gagal menghapus tanaman");
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const handleEdit = (item) => {
    setEditingTanaman(item);
    setShowForm(true);
    window.scrollTo({ top: 0, behavior: "smooth" });
  };

  return (
    <div className="p-6">
      {/* Header & Tombol Tambah */}
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-2xl font-bold text-green-800">Jenis Tanaman</h1>
        <button
          onClick={() => setShowForm(!showForm)}
          className="bg-green-600 hover:bg-green-700 text-white px-4 py-2 rounded"
        >
          {showForm ? "Batal" : "+ Tambah Tanaman"}
        </button>
      </div>

      {/* Error message */}
      {error && (
        <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
          {error}
        </div>
      )}

      {/* Loading indicator */}
      {loading && (
        <div className="flex justify-center my-4">
          <div className="animate-spin rounded-full h-10 w-10 border-t-2 border-b-2 border-green-500"></div>
        </div>
      )}

      {showForm && (
        <div className="mb-6">
          <TanamanForm
            onSubmit={handleAddOrEdit}
            initialData={editingTanaman}
            isEdit={!!editingTanaman}
          />
        </div>
      )}

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
        {tanamanList.map((tanaman) => (
          <TanamanCard
            key={tanaman.id}
            tanaman={tanaman}
            onDelete={handleDelete}
            onEdit={handleEdit}
          />
        ))}
      </div>

      {/* Empty state */}
      {!loading && tanamanList.length === 0 && (
        <div className="text-center py-10">
          <p className="text-gray-500">Belum ada data tanaman. Silakan tambahkan.</p>
        </div>
      )}
    </div>
  );
};

export default Tanaman;
