import { Link, useNavigate } from "react-router-dom";
import plantcareImage from '../images/plantcare.png';
import { useAuth } from "../context/AuthContext";

const Navbar = () => {
  const { user, logout } = useAuth();
  const navigate = useNavigate();
  
  const handleLogout = async (e) => {
    e.preventDefault();
    if (window.confirm('Apakah Anda yakin ingin logout?')) {
      await logout();
      navigate('/login');
    }
  };

  return (
    <nav className="bg-white border-b border-gray-200 text-green-900 p-3 flex items-center gap-6 shadow-md">
      {/* Logo dan Nama Aplikasi */}
      <Link to="/dashboard" className="flex items-center gap-2 ml-4">
        <img src={plantcareImage} alt="PlantCare Logo" className="w-12 h-12" />
        <span className="font-bold text-xl text-green-700">PlantCare</span>
      </Link>

      {/* Navigasi Menu */}
      <div className="flex items-center gap-6">
        <Link to="/dashboard" className="hover:text-green-600 hover:underline transition-colors">
          ðŸ“Š Dashboard
        </Link>
        <Link to="/tanaman" className="hover:text-green-600 hover:underline transition-colors">
          ðŸŒ± Tanaman
        </Link>
        <Link to="/jadwal" className="hover:text-green-600 hover:underline transition-colors">
          ðŸ“… Jadwal
        </Link>
      </div>
      
      <div className="ml-auto mr-8 flex items-center gap-4">
        <span className="text-gray-600">
          Halo, <span className="font-semibold text-green-700">{user?.username || 'User'}</span>! ðŸ‘‹
        </span>
        <button 
          onClick={handleLogout}
          className="bg-red-500 hover:bg-red-600 text-white px-4 py-2 rounded-lg transition-colors"
        >
          ðŸšª Logout
        </button>
      </div>
    </nav>
  );
};

export default Navbar;
