import { useState } from "react";
import TanamanForm from "../components/TanamanForm";
import TanamanCard from "../components/TanamanCard";
import useLocalStorage from "../hooks/useLocalStorage";

const Tanaman = () => {
  const [tanamanList, setTanamanList] = useLocalStorage("tanaman", []);
  const [editingTanaman, setEditingTanaman] = useState(null);
  const [showForm, setShowForm] = useState(false);

  const handleAddOrEdit = (data) => {
    if (editingTanaman) {
      const updated = tanamanList.map((item) =>
        item.id === data.id ? data : item
      );
      setTanamanList(updated);
      setEditingTanaman(null);
    } else {
      setTanamanList([...tanamanList, data]);
    }
    setShowForm(false); // Sembunyikan form setelah tambah/edit
  };

  const handleDelete = (id) => {
    const filtered = tanamanList.filter((item) => item.id !== id);
    setTanamanList(filtered);
  };

  const handleEdit = (item) => {
    setEditingTanaman(item);
    setShowForm(true);
    window.scrollTo({ top: 0, behavior: "smooth" });
  };

  const handleCancel = () => {
    setEditingTanaman(null);
    setShowForm(false);
  };

  return (
    <div className="p-6">
      {/* Header & Tombol Tambah */}
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-2xl font-bold text-green-800">Jadwal Perawatan</h1>
        <button
          onClick={() => setShowForm(!showForm)}
          className="bg-yellow-600 hover:bg-yellow-700 text-white px-4 py-2 rounded"
        >
          {showForm ? "Batal" : "+ Tambah Jadwal"}
        </button>
      </div>

      {showForm && (
        <TanamanForm
          onSubmit={handleAddOrEdit}
          initialData={editingTanaman}
          isEdit={!!editingTanaman}
        />
      )}

      {showForm && (
        <button
          onClick={handleCancel}
          className="text-sm text-red-600 underline ml-2"
        >
          Batal
        </button>
      )}

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
        {tanamanList.map((tanaman) => (
          <TanamanCard
            key={tanaman.id}
            tanaman={tanaman}
            onDelete={handleDelete}
            onEdit={handleEdit}
          />
        ))}
      </div>
    </div>
  );
};

export default Tanaman;
