// src/pages/Login.jsx

import { useState } from "react";
import { useNavigate } from "react-router-dom";
import { authApi } from "../services/api";
import { useAuth } from "../context/AuthContext";

const Login = () => {

  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

  const { login: setAuthUser } = useAuth();
  const navigate = useNavigate();
  const handleLogin = async (e) => {
    e.preventDefault();
    setError("");
    try {
      if (!username.trim() || !password.trim()) {
        setError("Username dan password wajib diisi");
        return;
      }
      
      console.log("Attempting login with:", { username });
      const data = await authApi.login(username, password);
      
      if (data.status === 'success') {
        console.log("Login successful:", data);
        setAuthUser({ username: username }); // Use username from form
        navigate("/dashboard");
      } else {
        setError(data.msg || "Login gagal - kredensial tidak valid");
      }
    } catch (err) {
      console.error("Login error:", err);
      setError(err.message || "Login gagal - tidak dapat terhubung ke server");
    }
  };

  return (
    <div className="flex justify-center items-center min-h-screen bg-green-50">
      <form
        onSubmit={handleLogin}
        className="bg-white p-6 rounded-xl shadow-md w-80"
      >
        <h2 className="text-xl font-bold text-center mb-4 text-green-700">
          Login PlantCare
        </h2>

        <label className="block text-sm mb-1 text-gray-700">Username</label>
        <input
          type="text"
          className="w-full border px-3 py-2 rounded mb-4"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          autoComplete="username"
        />
        <label className="block text-sm mb-1 text-gray-700">Password</label>
        <input
          type="password"
          className="w-full border px-3 py-2 rounded mb-4"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          autoComplete="current-password"
        />        {error && (
          <div className="text-red-600 text-sm mb-2 p-2 bg-red-50 border border-red-200 rounded">
            <strong>Error:</strong> {error}
          </div>
        )}
        <button
          type="submit"
          className="w-full bg-green-600 hover:bg-green-700 text-white px-4 py-2 rounded mt-2"
        >
          Login
        </button>
        
        <div className="mt-4 text-xs text-gray-500">
          <p><strong>Hint:</strong> Gunakan kredensial berikut untuk testing:</p>
          <p>Username: admin</p>
          <p>Password: sainsdata</p>
        </div>
      </form>
    </div>
  );
};

export default Login;
