import { useState, useEffect, useCallback } from "react";
import JadwalCard from "../components/JadwalCard";
import JadwalForm from "../components/JadwalForm";
import { jadwalApi } from "../services/api";

const Jadwal = () => {  const [jadwalList, setJadwalList] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [editData, setEditData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [filters, setFilters] = useState({
    tanaman: '',
    tanggal: ''
  });
  const [pagination, setPagination] = useState({ 
    limit: 10, 
    offset: 0, 
    total: 0 
  });
  // Fetch jadwal data when component mounts or pagination/filters change
  useEffect(() => {
    fetchJadwal();
  }, [pagination.offset, pagination.limit, filters.tanaman, filters.tanggal, fetchJadwal]);
  
  // Function to fetch jadwal data with pagination and filters
  const fetchJadwal = async () => {
    setLoading(true);
    setError(null);
    try {
      console.log(`Fetching jadwal data (limit: ${pagination.limit}, offset: ${pagination.offset})...`);
      console.log(`Filters: ${JSON.stringify(filters)}`);
      
      const response = await jadwalApi.getAll(
        pagination.limit, 
        pagination.offset, 
        filters.tanaman, 
        filters.tanggal
      );
      console.log("Received jadwal data:", response);
      
      if (response && response.jadwal && Array.isArray(response.jadwal)) {
        setJadwalList(response.jadwal);
        setPagination(prev => ({
          ...prev,
          total: response.pagination?.total || prev.total
        }));
      } else {
        setJadwalList([]);
      }
    } catch (err) {
      console.error("Error fetching jadwal:", err);
      setError(`Gagal mengambil data jadwal: ${err.message || "Unknown error"}`);
      // Set empty array to avoid undefined errors
      setJadwalList([]);
    } finally {
      setLoading(false);
    }
  };
  const handleTambah = async (jadwalBaru) => {
    setLoading(true);
    setError(null);
    try {
      console.log("Submitting jadwal:", jadwalBaru);
      const response = await jadwalApi.create(jadwalBaru);
      console.log("Created jadwal response:", response);
      fetchJadwal(); // Refresh data after adding
      setShowForm(false);
    } catch (err) {
      console.error("Error details:", err);
      setError(`Gagal menambahkan jadwal: ${err.message || "Unknown error"}`);
    } finally {
      setLoading(false);
    }
  };

  const handleEdit = async (updatedJadwal) => {
    setLoading(true);
    setError(null);
    try {
      await jadwalApi.update(updatedJadwal.id, updatedJadwal);
      fetchJadwal(); // Refresh data after updating
      setEditData(null);
      setShowForm(false);
    } catch (err) {
      setError("Gagal mengupdate jadwal");
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm("Apakah Anda yakin ingin menghapus jadwal ini?")) {
      return;
    }

    setLoading(true);
    setError(null);
    try {
      await jadwalApi.delete(id);
      fetchJadwal(); // Refresh data after deleting
    } catch (err) {
      setError("Gagal menghapus jadwal");
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const startEdit = (jadwal) => {
    setEditData(jadwal);
    setShowForm(true);
    window.scrollTo({ top: 0, behavior: "smooth" });
  };

  return (
    <div className="p-6">
      {/* Header & Tombol Tambah */}
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-2xl font-bold text-green-800">Jadwal Perawatan</h1>
        <button
          onClick={() => setShowForm(!showForm)}
          className="bg-yellow-600 hover:bg-yellow-700 text-white px-4 py-2 rounded"
        >
          {showForm ? "Batal" : "+ Tambah Jadwal"}
        </button>
      </div>

      {/* Error message */}
      {error && (
        <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
          {error}
        </div>
      )}

      {/* Loading indicator */}
      {loading && (
        <div className="flex justify-center my-4">
          <div className="animate-spin rounded-full h-10 w-10 border-t-2 border-b-2 border-yellow-500"></div>
        </div>
      )}      {/* Filter Section */}
      <div className="mb-6 bg-white p-4 rounded shadow">
        <h2 className="text-lg font-semibold text-gray-800 mb-3">Filter Jadwal</h2>
        <div className="flex flex-wrap gap-4">
          <div className="flex-1">
            <label className="block text-sm font-medium text-gray-700 mb-1">Nama Tanaman</label>
            <input 
              type="text" 
              className="w-full border border-gray-300 rounded px-3 py-2"
              value={filters.tanaman}
              onChange={(e) => setFilters(prev => ({ ...prev, tanaman: e.target.value }))}
              placeholder="Cari berdasarkan nama tanaman"
            />
          </div>
          <div className="flex-1">
            <label className="block text-sm font-medium text-gray-700 mb-1">Tanggal</label>
            <input 
              type="date" 
              className="w-full border border-gray-300 rounded px-3 py-2"
              value={filters.tanggal}
              onChange={(e) => setFilters(prev => ({ ...prev, tanggal: e.target.value }))}
            />
          </div>
          <div className="flex items-end">
            <button
              onClick={() => setFilters({ tanaman: '', tanggal: '' })}
              className="bg-gray-200 hover:bg-gray-300 text-gray-800 px-4 py-2 rounded"
            >
              Reset
            </button>
          </div>
        </div>
      </div>
      
      {/* Form Tambah/Edit */}
      {showForm && (
        <div className="mb-6">
          <JadwalForm
            onSubmit={editData ? handleEdit : handleTambah}
            initialData={editData}
            isEdit={!!editData}
          />
        </div>
      )}

      {/* Daftar Jadwal */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
        {jadwalList.map((jadwal) => (
          <JadwalCard
            key={jadwal.id}
            jadwal={jadwal}
            onEdit={() => startEdit(jadwal)}
            onDelete={() => handleDelete(jadwal.id)}
          />
        ))}
      </div>      {/* Empty state */}
      {!loading && jadwalList.length === 0 && (
        <div className="text-center py-10">
          <p className="text-gray-500">
            Belum ada jadwal perawatan. Silakan tambahkan.
          </p>
        </div>
      )}
      
      {/* Pagination controls */}
      <div className="flex justify-between items-center mt-6">
        <div>
          <button
            onClick={() => {
              if (pagination.offset > 0) {
                setPagination(prev => ({
                  ...prev,
                  offset: Math.max(0, prev.offset - prev.limit)
                }));
              }
            }}
            disabled={pagination.offset === 0}
            className="bg-yellow-600 hover:bg-yellow-700 text-white px-4 py-2 rounded-l"
          >
            &laquo; Sebelumnya
          </button>
          <button
            onClick={() => {
              if (pagination.offset + pagination.limit < pagination.total) {
                setPagination(prev => ({
                  ...prev,
                  offset: prev.offset + prev.limit
                }));
              }
            }}
            disabled={pagination.offset + pagination.limit >= pagination.total}
            className="bg-yellow-600 hover:bg-yellow-700 text-white px-4 py-2 rounded-r"
          >
            Selanjutnya &raquo;
          </button>
        </div>

        {/* Items per page selector */}
        <div>
          <label className="text-gray-700 mr-2">
            Tampilkan:
            <select
              value={pagination.limit}
              onChange={(e) => {
                const newLimit = parseInt(e.target.value);
                setPagination(prev => ({
                  ...prev,
                  limit: newLimit,
                  offset: 0 // Reset to first page when changing items per page
                }));
              }}
              className="ml-2 bg-white border border-gray-300 rounded"
            >
              <option value={5}>5</option>
              <option value={10}>10</option>
              <option value={20}>20</option>
            </select>
            &nbsp;data
          </label>
        </div>
      </div>
    </div>
  );
};

export default Jadwal;
