import { Link } from 'react-router-dom';
import { useState, useEffect } from 'react';
import { getDashboardSummary } from '../api/dashboardApi';

const Dashboard = () => {
  const [dashboardData, setDashboardData] = useState({
    totalTanaman: 0,
    totalJadwal: 0,
    totalJadwalHariIni: 0,
    recentPlants: [],
    upcomingSchedules: [],
    plantStats: [],
    systemStatus: {}
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchDashboardData = async () => {
      try {
        setLoading(true);
        const data = await getDashboardSummary();
        setDashboardData(data);
        if (data.error) {
          setError(data.error);
        }
      } catch (err) {
        setError('Failed to load dashboard data');
        console.error('Dashboard fetch error:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchDashboardData();
  }, []);

  if (loading) {
    return (
      <div className="p-6">
        <div className="flex justify-center items-center h-64">
          <div className="text-lg text-gray-600">Loading dashboard...</div>
        </div>
      </div>
    );
  }
  return (
    <div className="p-6">
      <h1 className="text-3xl font-bold text-green-800 mb-4">Selamat datang di PlantCare! 🌿</h1>
      
      {error && (
        <div className="bg-yellow-100 border border-yellow-400 text-yellow-700 px-4 py-3 rounded mb-4">
          <strong>Warning:</strong> {error}. Showing limited data.
        </div>
      )}

      <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mt-6">
        <div className="bg-white shadow-md rounded-xl p-6 border-l-4 border-green-500">
          <h2 className="text-lg font-semibold text-gray-700">Total Tanaman</h2>
          <p className="text-3xl font-bold text-green-700">{dashboardData.totalTanaman}</p>
          <Link to="/tanaman" className="text-green-600 mt-2 inline-block hover:underline">
            Lihat semua tanaman →
          </Link>
        </div>

        <div className="bg-white shadow-md rounded-xl p-6 border-l-4 border-yellow-500">
          <h2 className="text-lg font-semibold text-gray-700">Jadwal Hari Ini</h2>
          <p className="text-3xl font-bold text-yellow-700">{dashboardData.totalJadwalHariIni}</p>
          <Link to="/jadwal" className="text-yellow-600 mt-2 inline-block hover:underline">
            Cek jadwal perawatan →
          </Link>
        </div>

        <div className="bg-white shadow-md rounded-xl p-6 border-l-4 border-blue-500">
          <h2 className="text-lg font-semibold text-gray-700">Total Jadwal</h2>
          <p className="text-3xl font-bold text-blue-700">{dashboardData.totalJadwal}</p>
          <Link to="/jadwal" className="text-blue-600 mt-2 inline-block hover:underline">
            Kelola semua jadwal →
          </Link>
        </div>
      </div>

      {/* Recent Plants */}
      {dashboardData.recentPlants && dashboardData.recentPlants.length > 0 && (
        <div className="mt-8">
          <h2 className="text-2xl font-bold text-gray-800 mb-4">Tanaman Terbaru</h2>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
            {dashboardData.recentPlants.slice(0, 3).map((plant) => (
              <div key={plant.id} className="bg-white shadow-md rounded-lg p-4 border">
                <h3 className="font-semibold text-gray-800">{plant.nama}</h3>
                <p className="text-gray-600 text-sm">{plant.jenis}</p>
                <p className="text-gray-500 text-xs">{plant.lokasi}</p>
                <p className="text-gray-400 text-xs mt-2">Ditambahkan: {new Date(plant.created_at).toLocaleDateString('id-ID')}</p>
              </div>
            ))}
          </div>
        </div>
      )}

      {/* Upcoming Schedules */}
      {dashboardData.upcomingSchedules && dashboardData.upcomingSchedules.length > 0 && (
        <div className="mt-8">
          <h2 className="text-2xl font-bold text-gray-800 mb-4">Jadwal Mendatang</h2>
          <div className="bg-white shadow-md rounded-lg p-4">
            {dashboardData.upcomingSchedules.slice(0, 5).map((schedule) => (
              <div key={schedule.id} className="flex justify-between items-center py-2 border-b last:border-b-0">
                <div>
                  <span className="font-medium text-gray-800">{schedule.namaTanaman}</span>
                  <span className="text-gray-600 ml-2">- {schedule.kegiatan}</span>
                </div>
                <span className="text-gray-500 text-sm">{new Date(schedule.tanggal).toLocaleDateString('id-ID')}</span>
              </div>
            ))}
          </div>
        </div>
      )}

      {/* Plant Statistics */}
      {dashboardData.plantStats && dashboardData.plantStats.length > 0 && (
        <div className="mt-8">
          <h2 className="text-2xl font-bold text-gray-800 mb-4">Statistik Tanaman</h2>
          <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
            {dashboardData.plantStats.map((stat, index) => (
              <div key={index} className="bg-white shadow-md rounded-lg p-4 text-center">
                <h3 className="font-semibold text-gray-800 text-sm">{stat.jenis}</h3>
                <p className="text-2xl font-bold text-green-600">{stat.jumlah}</p>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
