import { useState } from "react";

const TanamanForm = ({ onSubmit, initialData = {}, isEdit = false }) => {
  const [nama, setNama] = useState(initialData.nama || "");
  const [jenis, setJenis] = useState(initialData.jenis || "");
  const [lokasi, setLokasi] = useState(initialData.lokasi || "");

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!nama || !jenis || !lokasi) return;
    onSubmit({ id: initialData.id || Date.now(), nama, jenis, lokasi });
    // reset form jika tambah
    if (!isEdit) {
      setNama("");
      setJenis("");
      setLokasi("");
    }
  };

  return (
    <form onSubmit={handleSubmit} className="bg-white p-6 rounded-xl shadow-md space-y-4">
      <h2 className="text-xl font-semibold text-green-700">
        {isEdit ? "Edit Tanaman" : "Tambah Tanaman"}
      </h2>

      <div>
        <label className="block text-sm text-gray-700">Nama Tanaman</label>
        <input
          type="text"
          className="w-full border rounded px-3 py-2 mt-1"
          value={nama}
          onChange={(e) => setNama(e.target.value)}
        />
      </div>

      <div>
        <label className="block text-sm text-gray-700">Jenis</label>
        <input
          type="text"
          className="w-full border rounded px-3 py-2 mt-1"
          value={jenis}
          onChange={(e) => setJenis(e.target.value)}
        />
      </div>

      <div>
        <label className="block text-sm text-gray-700">Lokasi</label>
        <input
          type="text"
          className="w-full border rounded px-3 py-2 mt-1"
          value={lokasi}
          onChange={(e) => setLokasi(e.target.value)}
        />
      </div>

      <button
        type="submit"
        className="bg-green-600 hover:bg-green-700 text-white px-4 py-2 rounded"
      >
        {isEdit ? "Simpan Perubahan" : "Tambah"}
      </button>
    </form>
  );
};

export default TanamanForm;
