// BASE URL API
// Using a relative URL can help avoid CORS issues
const API_URL = '/api';
// If relative URL doesn't work, uncomment and use the absolute URL:
// const API_URL = 'http://localhost:6543/api';

// TANAMAN API
export const tanamanApi = {
    // Ambil semua tanaman
    getAll: async () => {
        try {
            const response = await fetch(`${API_URL}/tanaman`);
            if (!response.ok) {
                throw new Error(`Error: ${response.status}`);
            }
            const data = await response.json();
            return data.tanaman;
        } catch (error) {
            console.error("Error fetching tanaman:", error);
            throw error;
        }
    },

    // Tambah tanaman baru
    create: async (tanamanData) => {
        try {
            const response = await fetch(`${API_URL}/tanaman`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(tanamanData)
            });
            if (!response.ok) {
                throw new Error(`Error: ${response.status}`);
            }
            return await response.json();
        } catch (error) {
            console.error("Error creating tanaman:", error);
            throw error;
        }
    },

    // Update tanaman
    update: async (id, tanamanData) => {
        try {
            const response = await fetch(`${API_URL}/tanaman/${id}`, {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(tanamanData)
            });
            if (!response.ok) {
                throw new Error(`Error: ${response.status}`);
            }
            return await response.json();
        } catch (error) {
            console.error(`Error updating tanaman ${id}:`, error);
            throw error;
        }
    },

    // Hapus tanaman
    delete: async (id) => {
        try {
            const response = await fetch(`${API_URL}/tanaman/${id}`, {
                method: 'DELETE'
            });
            if (!response.ok) {
                throw new Error(`Error: ${response.status}`);
            }
            return await response.json();
        } catch (error) {
            console.error(`Error deleting tanaman ${id}:`, error);
            throw error;
        }
    }
};

// JADWAL API
export const jadwalApi = {    // Ambil semua jadwal
    getAll: async () => {
        try {
            console.log("Fetching jadwal data from API");
            const response = await fetch(`${API_URL}/jadwal`, {
                method: 'GET',
                headers: {
                    'Accept': 'application/json'
                }
            });
            
            if (!response.ok) {
                console.error(`API Error: ${response.status}`);
                throw new Error(`Error: ${response.status}`);
            }
            
            const data = await response.json();
            console.log("API Response:", data);
            
            if (!data.jadwal || !Array.isArray(data.jadwal)) {
                console.error("Invalid data format received:", data);
                return [];
            }
            
            return data.jadwal;
        } catch (error) {
            console.error("Error fetching jadwal:", error);
            throw error;
        }
    },// Tambah jadwal baru
    create: async (jadwalData) => {
        try {
            console.log("Sending jadwal data:", jadwalData);  // Log data being sent
              // Ensure proper formatting of the data
            const formattedData = {
                namaTanaman: jadwalData.namaTanaman,
                kegiatan: jadwalData.kegiatan,
                tanggal: jadwalData.tanggal
            };
            
            console.log("Formatted data:", formattedData);
            console.log("API URL:", API_URL);
            
            const response = await fetch(`${API_URL}/jadwal`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Accept': 'application/json'
                },
                body: JSON.stringify(formattedData),
                // Remove credentials to resolve CORS issues
                mode: 'cors'
            });
            
            // Handle non-OK responses
            if (!response.ok) {
                let errorMessage;
                try {
                    // Try to get a JSON error message
                    const errorData = await response.json();
                    errorMessage = errorData.error || `Error: ${response.status}`;
                } catch {
                    // If response isn't JSON, just use text
                    const errorText = await response.text();
                    errorMessage = `Error: ${response.status} - ${errorText || 'Unknown error'}`;
                }
                throw new Error(errorMessage);
            }
            
            return await response.json();
        } catch (error) {
            console.error("Error creating jadwal:", error);
            throw error;
        }
    },

    // Update jadwal
    update: async (id, jadwalData) => {
        try {
            const response = await fetch(`${API_URL}/jadwal/${id}`, {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(jadwalData)
            });
            if (!response.ok) {
                throw new Error(`Error: ${response.status}`);
            }
            return await response.json();
        } catch (error) {
            console.error(`Error updating jadwal ${id}:`, error);
            throw error;
        }
    },

    // Hapus jadwal
    delete: async (id) => {
        try {
            const response = await fetch(`${API_URL}/jadwal/${id}`, {
                method: 'DELETE'
            });
            if (!response.ok) {
                throw new Error(`Error: ${response.status}`);
            }
            return await response.json();
        } catch (error) {
            console.error(`Error deleting jadwal ${id}:`, error);
            throw error;
        }
    }
};
