import { useState } from "react";
import TanamanCard from "../components/TanamanCard";
import TanamanForm from "../components/TanamanForm";

const Tanaman = () => {
  const [tanamanList, setTanamanList] = useState([
    { id: 1, nama: "Monstera", jenis: "Daun Hias", lokasi: "Ruang Tamu" },
    { id: 2, nama: "Lidah Mertua", jenis: "Indoor", lokasi: "Kamar" },
  ]);

  const [showForm, setShowForm] = useState(false);

  const handleTambah = (tanamanBaru) => {
    setTanamanList((prev) => [...prev, tanamanBaru]);
    setShowForm(false); // sembunyikan form setelah tambah
  };

  return (
    <div className="p-6">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-2xl font-bold text-green-800">Daftar Tanaman</h1>
        <button
          onClick={() => setShowForm(!showForm)}
          className="bg-green-600 hover:bg-green-700 text-white px-4 py-2 rounded"
        >
          {showForm ? "Batal" : "+ Tambah Tanaman"}
        </button>
      </div>

      {showForm && (
        <div className="mb-6">
          <TanamanForm onSubmit={handleTambah} />
        </div>
      )}

      <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-3">
        {tanamanList.map((tanaman) => (
          <TanamanCard key={tanaman.id} tanaman={tanaman} />
        ))}
      </div>
    </div>
  );
};

export default Tanaman;
