const TanamanCard = ({ tanaman, onDelete, onEdit }) => {
  return (
    <div className="border p-4 rounded shadow space-y-2">
      <h3 className="text-lg font-bold">{tanaman.nama}</h3>
      <p><strong>Jenis:</strong> {tanaman.jenis}</p>
      <p><strong>Lokasi:</strong> {tanaman.lokasi}</p>
      <div className="flex gap-2">
        <button
          onClick={() => onEdit(tanaman)}
          className="bg-yellow-500 hover:bg-yellow-600 text-white px-3 py-1 rounded"
        >
          Edit
        </button>
        <button
          onClick={() => onDelete(tanaman.id)}
          className="bg-red-500 hover:bg-red-600 text-white px-3 py-1 rounded"
        >
          Hapus
        </button>
      </div>
    </div>
  );
};

export default TanamanCard;
