// TANAMAN API
export const tanamanApi = {    // Ambil semua tanaman
    getAll: async () => {
        try {
            console.log("Fetching tanaman data from API:", `${API_URL}/tanaman`);
            const response = await fetch(`${API_URL}/tanaman`, {
                method: 'GET',
                headers: {
                    'Accept': 'application/json'
                },
                mode: 'cors' // Important for CORS requests
            });
            
            if (!response.ok) {
                const errorText = await response.text();
                console.error(`API Error (${response.status}):`, errorText);
                throw new Error(`Error: ${response.status}`);
            }
            
            const data = await response.json();
            console.log("API Response for tanaman:", data);
            
            if (!data.tanaman || !Array.isArray(data.tanaman)) {
                console.error("Invalid data format received for tanaman:", data);
                return [];
            }
            
            return data.tanaman;
        } catch (error) {
            console.error("Error fetching tanaman:", error);
            throw error;
        }
    },

    // Tambah tanaman baru
    create: async (tanamanData) => {
        try {
            const response = await fetch(`${API_URL}/tanaman`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
