import { useState } from "react";
import JadwalCard from "../components/JadwalCard";
import JadwalForm from "../components/JadwalForm";

const [showForm, setShowForm] = useState(false);


const Jadwal = () => {
  const [jadwalList, setJadwalList] = useState([
    {
      id: 1,
      namaTanaman: "Monstera",
      tanggal: "2025-05-15",
      kegiatan: "Penyiraman",
    },
    {
      id: 2,
      namaTanaman: "Lidah Mertua",
      tanggal: "2025-05-16",
      kegiatan: "Pemupukan",
    },
  ]);

  return (
    <div className="p-6">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-2xl font-bold text-green-800">Jadwal Perawatan</h1>
        <button className="bg-yellow-600 hover:bg-yellow-700 text-white px-4 py-2 rounded">
          + Tambah Jadwal
        </button>
      </div>

      <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-3">
        {jadwalList.map((jadwal) => (
          <JadwalCard key={jadwal.id} jadwal={jadwal} />
        ))}
      </div>
    </div>
  );
};

export default Jadwal;
