const JadwalCard = ({ jadwal, onDelete, onEdit }) => {
  return (
    <div className="bg-white p-4 rounded-xl shadow hover:shadow-lg border-l-4 border-yellow-400">
      <h3 className="text-lg font-bold text-yellow-800">{jadwal.namaTanaman}</h3>
      <p>Kegiatan: {jadwal.kegiatan}</p>
      <p>Tanggal: {jadwal.tanggal}</p>

      <div className="flex gap-2 mt-2">
        <button
          onClick={() => onEdit(jadwal)}
          className="bg-yellow-500 hover:bg-yellow-600 text-white px-3 py-1 rounded"
        >
          Edit
        </button>
        <button
          onClick={() => onDelete(jadwal.id)}
          className="bg-red-500 hover:bg-red-600 text-white px-3 py-1 rounded"
        >
          Hapus
        </button>
      </div>
    </div>
  );
};

export default JadwalCard;
