const TanamanCard = ({ tanaman, onDelete, onEdit }) => {
  return (
    <div className="bg-white p-4 rounded-xl shadow hover:shadow-lg border-l-4 border-green-400">
      <h3 className="text-lg font-bold text-green-800">{tanaman.nama}</h3>
      <p>Jenis: {tanaman.jenis}</p>
      <p>Lokasi: {tanaman.lokasi}</p>

      <div className="flex gap-2 mt-2">
        <button
          onClick={() => onEdit(tanaman)}
          className="bg-yellow-500 hover:bg-yellow-600 text-white px-3 py-1 rounded"
        >
          Edit
        </button>
        <button
          onClick={() => onDelete(tanaman.id)}
          className="bg-red-500 hover:bg-red-600 text-white px-3 py-1 rounded"
        >
          Hapus
        </button>
      </div>
    </div>
  );
};

export default TanamanCard;
