import { useState, useEffect } from "react";
import JadwalCard from "../components/JadwalCard";
import JadwalForm from "../components/JadwalForm";
import { jadwalApi } from "../services/api";

const Jadwal = () => {
  const [jadwalList, setJadwalList] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [editData, setEditData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Fetch jadwal data when component mounts
  useEffect(() => {
    fetchJadwal();
  }, []);

  // Function to fetch jadwal data
  const fetchJadwal = async () => {
    setLoading(true);
    setError(null);
    try {
      const data = await jadwalApi.getAll();
      setJadwalList(data);
    } catch (err) {
      setError("Gagal mengambil data jadwal");
      console.error(err);
    } finally {
      setLoading(false);
    }
  };
  const handleTambah = async (jadwalBaru) => {
    setLoading(true);
    setError(null);
    try {
      console.log("Submitting jadwal:", jadwalBaru);
      const response = await jadwalApi.create(jadwalBaru);
      console.log("Created jadwal response:", response);
      fetchJadwal(); // Refresh data after adding
      setShowForm(false);
    } catch (err) {
      console.error("Error details:", err);
      setError(`Gagal menambahkan jadwal: ${err.message || "Unknown error"}`);
    } finally {
      setLoading(false);
    }
  };

  const handleEdit = async (updatedJadwal) => {
    setLoading(true);
    setError(null);
    try {
      await jadwalApi.update(updatedJadwal.id, updatedJadwal);
      fetchJadwal(); // Refresh data after updating
      setEditData(null);
      setShowForm(false);
    } catch (err) {
      setError("Gagal mengupdate jadwal");
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm("Apakah Anda yakin ingin menghapus jadwal ini?")) {
      return;
    }

    setLoading(true);
    setError(null);
    try {
      await jadwalApi.delete(id);
      fetchJadwal(); // Refresh data after deleting
    } catch (err) {
      setError("Gagal menghapus jadwal");
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const startEdit = (jadwal) => {
    setEditData(jadwal);
    setShowForm(true);
    window.scrollTo({ top: 0, behavior: "smooth" });
  };

  return (
    <div className="p-6">
      {/* Header & Tombol Tambah */}
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-2xl font-bold text-green-800">Jadwal Perawatan</h1>
        <button
          onClick={() => setShowForm(!showForm)}
          className="bg-yellow-600 hover:bg-yellow-700 text-white px-4 py-2 rounded"
        >
          {showForm ? "Batal" : "+ Tambah Jadwal"}
        </button>
      </div>

      {/* Error message */}
      {error && (
        <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
          {error}
        </div>
      )}

      {/* Loading indicator */}
      {loading && (
        <div className="flex justify-center my-4">
          <div className="animate-spin rounded-full h-10 w-10 border-t-2 border-b-2 border-yellow-500"></div>
        </div>
      )}

      {/* Form Tambah/Edit */}
      {showForm && (
        <div className="mb-6">
          <JadwalForm
            onSubmit={editData ? handleEdit : handleTambah}
            initialData={editData}
            isEdit={!!editData}
          />
        </div>
      )}

      {/* Daftar Jadwal */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
        {jadwalList.map((jadwal) => (
          <JadwalCard
            key={jadwal.id}
            jadwal={jadwal}
            onEdit={() => startEdit(jadwal)}
            onDelete={() => handleDelete(jadwal.id)}
          />
        ))}
      </div>

      {/* Empty state */}
      {!loading && jadwalList.length === 0 && (
        <div className="text-center py-10">
          <p className="text-gray-500">
            Belum ada jadwal perawatan. Silakan tambahkan.
          </p>
        </div>
      )}
    </div>
  );
};

export default Jadwal;
